import { NativeBaseProvider, Button, Text, Input, Stack, Container} from "native-base";
import { ImageBackground, View, StyleSheet } from 'react-native';

export default function App() {
  return (
    <ImageBackground
          style={styles.backgroundImage}
          source={require("./Fundo.png")}
    >
    <NativeBaseProvider>
      <Container
        style={styles.container}
      >

          <View style={styles.square}></View>
          <View style={styles.content}>

          <Text color={'white'} style={{marginTop:10, fontSize:50}}>
            LOGIN
          </Text>

          <Text color={'white'} style={{marginTop:30}}>
            Email
          </Text>
          <Input variant="underlined" mx="3" placeholder="seuemail@gmail.com" w="75%" marginTop={1}/>

          <Text color={'white'} style={{marginTop:30}}>
            Senha
          </Text>
          <Input variant="underlined" mx="3" placeholder="Senha" w="75%" marginTop={1}/>

          <Button size="lg" marginTop={3} colorScheme={'black'} variant="ghost" width={'20%'}>
            ▶
          </Button>

          <Stack mb="2" mt="0.5">
            <Button size="m" fontSize={18} colorScheme={'white'} variant="link">
              Não possui conta ainda?
            </Button>
          </Stack>
          </View>
      </Container>
    </NativeBaseProvider>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width: "100%",
    height: "100%",
    alignItems: "center",
    justifyContent: "center",
  },
  backgroundImage: {
    flex: 1,
    resizeMode: 'cover',
  },
  square: {
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 10 },
    shadowOpacity: 1,
    shadowRadius: 20,
    elevation: 5,
    position: 'absolute',
    width: 350,
    height: 450,
    borderRadius: 25,
    backgroundColor: 'rgba(223, 159, 255, 0.33)',
  },
  content: {
    alignItems: 'center',
    justifyContent: 'center',
  },
});
