import { NativeBaseProvider, Button, Text, Input, Stack, Container} from "native-base";
import { ImageBackground, View, StyleSheet } from 'react-native';

export default function App() {
  return (
    <NativeBaseProvider>
      <Container>
        <ImageBackground
          style={styles.container}
          source={require("./Fundo.png")}
          >
          <View style={{alignItems:'center', height:300, width:'100%', marginTop:20, marginLeft:20, justifyContent:'center'}} >
          </View>

          <Text style={{marginTop:10, fontSize:15, color:'rgb(255, 128, 191)'}}>
            LOGIN
          </Text>

          <Text>
            Email
          </Text>
          <Input variant="underlined" mx="3" placeholder="seuemail@gmail.com" w="33%" marginTop={1}/>

          <Text>
            Senha
          </Text>
          <Input variant="underlined" mx="3" placeholder="Senha" w="33%" marginTop={1}/>

          <Button size="lg" marginTop={3} colorScheme={'pink'} variant="ghost" width={'20%'}>
            Entrar
          </Button>

          <Stack mb="2" mt="0.5">
            <Button size="m" fontSize={18} colorScheme={'black'} variant="link">
              NÃ£o possui conta ainda?
            </Button>
          </Stack>
        </ImageBackground>
      </Container>
    </NativeBaseProvider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width: "100%",
    height: "100%",
    alignItems: "center",
    justifyContent: "center",
  },
});
