import React from "react";
import { NativeBaseProvider, Button, Text, Input, Stack } from "native-base";
import styled from "styled-components/native";

const StyledContainer = styled.SafeAreaView`
  background-color: #ffffff;
  flex: 1;
  justify-content: center;
  align-items: center;
`;

export default function App() {
  return (
    <NativeBaseProvider>
      <StyledContainer>
        <Text style={{ marginTop: 30, fontSize: 15, color: "rgb(128, 128, 255)" }}>
          LOGIN
        </Text>
        <Text style={{ marginLeft: 29 }}>Email</Text>
        <Input
          variant="underlined"
          mx={3}
          placeholder="ex@gmail.com"
          w="70%"
          mt={1}
          style={{ marginLeft: "15%" }}
        />
        <Text style={{ marginLeft: 29 }}>Senha</Text>
        <Input
          variant="underlined"
          mx={3}
          placeholder="senha"
          w="70%"
          mt={1}
          style={{ marginLeft: "15%" }}
        />

        <Button
          size="lg"
          mt={3}
          colorScheme="emerald"
          variant="ghost"
          width="20%"
          style={{ marginLeft: "40%" }}
        >
          Entrar
        </Button>
        <Stack
          mb={2.5}
          mt={1.5}
          direction={{
            base: "column",
            md: "row",
          }}
          space={2}
          mx={{
            base: "auto",
            md: "0",
          }}
        >
          <Button size="m" fontSize={18} colorScheme="black" variant="link">
            NÃ£o tem conta ainda? Cadastre-se
          </Button>
        </Stack>
      </StyledContainer>
    </NativeBaseProvider>
  );
}
