import React from "react";
import { NativeBaseProvider, Button, Text, Input, Stack, Container } from "native-base";
import { ImageBackground, View, StyleSheet } from "react-native";

export default function App() {
  return (
    <ImageBackground
      style={styles.backgroundImage}
      source={require("./Fundo.png")}
    >
      <NativeBaseProvider>
        <View style={styles.container}>
          <Container style={styles.content}>
            <Text color="white" style={{ marginTop: 10, fontSize: 50 }}>
              LOGIN
            </Text>

            <Text color="white" style={{ marginTop: 30 }}>
              Email
            </Text>
            <Input variant="underlined" mx={3} placeholder="seuemail@gmail.com" w="100%" marginTop={1} />

            <Text color="white" style={{ marginTop: 30 }}>
              Senha
            </Text>
            <Input variant="underlined" mx={3} placeholder="Senha" w="100%" marginTop={1} />

            <Button size="lg" marginTop={3} colorScheme="danger.50" variant="ghost" width="20%">
              ▶
            </Button>

            <Stack mb={2} mt={0.5}>
              <Button size="m" fontSize={18} colorScheme="purple" variant="link">
                Não possui conta ainda?
              </Button>
            </Stack>
          </Container>
        </View>
      </NativeBaseProvider>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  backgroundImage: {
    flex: 1,
    resizeMode: "cover",
  },
  content: {
    alignItems: "center",
    justifyContent: "center",
  },
});
